import React from 'react';
import Header from "./Header";
import Main from "./Main";
import Footer from "./Footer";
import AddPlacePopup from "./AddPlacePopup";
import EditAvatarPopup from "./EditAvatarPopup";
import EditProfilePopup from "./EditProfilePopup";
import api from "../utils/api";
import ImagePopup from "./ImagePopup";
import {CurrentUserContext} from "../contexts/CurrentUserContext";
import { Route, Switch, useHistory } from 'react-router-dom';
import {Login} from "./Login";
import {Register} from "./Register";
import {ProtectedRoute} from "./ProtectedRoute";
import {InfoTooltip} from "./InfoTooltip";
import * as apiAuth from "../utils/apiAuth"

function App() {
    const [isEditAvatarPopupOpen, setEditAvatarPopupOpen] = React.useState(false);
    const [isEditProfilePopupOpen, setEditProfilePopupOpen] = React.useState(false);
    const [isAddPlacePopupOpen, setAddPlacePopupOpen] = React.useState(false);
    const [cards, setCards] = React.useState([])
    const [selectedCard, setSelectedCard] = React.useState({})
    const [isImagePopupOpen, setImagePopupOpen] = React.useState(false);
    const [currentUser, setCurrentUser] = React.useState({})
    const [isLoading, setIsLoading] = React.useState(false)
    const [isLoggedIn, setIsLoggedIn] = React.useState(false)
    const [email, setEmail] = React.useState('')
    const [isInfoTooltipOpen, setInfoTooltipOpen] = React.useState(false)
    const [tooltipStatus, setToolTipStatus] = React.useState(false)

    const history = useHistory();

    React.useEffect(() => {
      const jwt = localStorage.getItem('jwt');
      if(jwt){
        Promise.all([apiAuth.getContent(jwt), api.getInitialCards(), api.getUserInfo()])
            .then(([getContent, getInitialCards, getUserInfo]) => {
              setIsLoggedIn(true);
              setEmail(getContent.email)
              history.push('/')
              setCurrentUser(getUserInfo.res)
              setCards(getInitialCards.data)

            })
            .catch(err => console.error(err))
      }
    }, [history, isLoggedIn])

  // React.useEffect(() => {
  //   api.getUserInfo()
  //       .then(res => {
  //         setEmail(res.email);
  //         setCurrentUser(res)})
  //       .catch(err => {console.error(err)})
  // }, [])

  // React.useEffect(() => {
  //   api.getInitialCards()
  //       .then(res => {setCards(res.data)})
  //       .catch(err => {console.error(err)})
  // }, [])

  function register(email, password) {
    apiAuth.register(email, password)
        .then((res) => {
          if (res) {
            setToolTipStatus(false)
            setInfoTooltipOpen(true)
            history.push('/signin');
          }
        })
        .catch((err) => {
          setToolTipStatus(true)
          setInfoTooltipOpen(true)
          console.log(`Упс, произошла ошибка: ${err}`);
        })
  }

  function enter(email, password) {
    apiAuth.enter(email, password)
        .then((data) => {
          if (data.token) {
            setToolTipStatus(false)
            setInfoTooltipOpen(false)
            localStorage.setItem('jwt', data.token)
            setIsLoggedIn(true)
            history.push('/')
            setEmail(email)

          } else {
            setToolTipStatus(true)
            setInfoTooltipOpen(true)
          }
        })
        .catch((err) => {
          setToolTipStatus(true)
          setInfoTooltipOpen(true)
          console.log(`Упс, произошла ошибка: ${err}`);
        })
  }

  function signOut(){
    localStorage.removeItem('jwt');
    setIsLoggedIn(false);
    history.push('/signin');
  }


    const changeLoading = () =>{
      setIsLoading(true)
  }

    const handleCardClick = (data) => {
      setImagePopupOpen(true)
      setSelectedCard(data)
    }

    const closeAllPopups = () => {
        setEditAvatarPopupOpen(false)
        setEditProfilePopupOpen(false)
        setAddPlacePopupOpen(false)
        setImagePopupOpen(false)
        setInfoTooltipOpen(false)
    }

    const handleEditProfileClick = () => {
        setEditProfilePopupOpen(true)
    }
    const handleAddPlaceClick = () => {
        setAddPlacePopupOpen(true)
    }
    function handleEditAvatarClick(){
        setEditAvatarPopupOpen(true)
    }
    //Меняет информацию о пользователе
    const handleUpdateUser = (data) => {
      api.setUserInfo(data)
          .then((res) => {
            setCurrentUser(res.data)
            closeAllPopups()
          })
          .catch(err => {console.error(err)})
          .finally(() => {
            setIsLoading(false)
          })
    }
    //Меняет аватар
    const handleUpdateAvatar = (data) => {
      api.changeAvatar(data)
          .then((res) => {
            setCurrentUser(res.data)
            closeAllPopups()
          })
          .catch(err => {console.error(err)})
          .finally(() => {
            setIsLoading(false)
          })
    }
    //Добавление новой карточки
    const handleAddPlaceSubmit = (newCard) => {
      api.setCardServer(newCard)
          .then((res) => {
            setCards([res.data, ...cards]);
            closeAllPopups()
          })
          .catch(err => {console.error(err)})
          .finally(() => {
            setIsLoading(false)
          })
    }
  function handleCardLike(card) {
    // Снова проверяем, есть ли уже лайк на этой карточке
    const isLiked = card.likes.some(i => i === currentUser._id);

    // Отправляем запрос в API и получаем обновлённые данные карточки
    api.changeLikeCardStatus(card._id, !isLiked)
        .then((newCard) => {
          const newCards = cards.map((c) => c._id === card._id ? newCard.data : c);// Формируем новый массив на основе имеющегося, подставляя в него новую карточку
          //проверяет если id предыдущей карточки равен id полученной при PUT-запросе, то создавай новую карточку из запроса иначе оставляй старую
          setCards(newCards)
        })
        .catch(err => {console.error(err)})
  }

  function handleCardDelete(card){
    api.deleteCard(card._id)
        .then(() => {
          setCards(cards.filter((c) => c._id !== card._id))
        })
        .catch(err => {console.error(err)})
  }

  React.useEffect(() => {
    window.addEventListener('keydown', (e) => {
      if (e.key === 'Escape') {
        closeAllPopups();
      }
    })
  },[])

  function overlayClick(e) {
      if (e.classList.contains('popup')){
        closeAllPopups()
      }
  }

  return (
      <CurrentUserContext.Provider value={currentUser}>
          <div className="page">
            <Header signOut={signOut} email={email} />
            <Switch>
              <ProtectedRoute
                  exact path="/"
                  component={Main}
                  onEditAvatar={handleEditAvatarClick}
                  onEditProfile={handleEditProfileClick}
                  onAddPlace={handleAddPlaceClick}
                  onCardClick={handleCardClick}
                  onCardLike={handleCardLike}
                  onCardDelete={handleCardDelete}
                  cards={cards}
                  setCards={setCards}
                  isLoggedIn={isLoggedIn}
              />
              <Route path="/signup">
                <Register register={register}/>
              </Route>
              <Route path="/signin">
                <Login enter={enter} />
              </Route>
            </Switch>
            <InfoTooltip
                onClose={closeAllPopups}
                isOpen={isInfoTooltipOpen}
                overlay={overlayClick}
                tooltipStatus={tooltipStatus}
            />
            <AddPlacePopup
                isLoading={isLoading}
                onChangeLoading={changeLoading}
                overlay={overlayClick}
                onAddPlace={handleAddPlaceSubmit}
                isOpen={isAddPlacePopupOpen}
                onClose={closeAllPopups}
            />
            <EditAvatarPopup
                isLoading={isLoading}
                onChangeLoading={changeLoading}
                overlay={overlayClick}
                onUpdateAvatar={handleUpdateAvatar}
                isOpen={isEditAvatarPopupOpen}
                onClose={closeAllPopups}
            />
            <EditProfilePopup
                isLoading={isLoading}
                onChangeLoading={changeLoading}
                overlay={overlayClick}
                onUpdateUser={handleUpdateUser}
                isOpen={isEditProfilePopupOpen}
                onClose={closeAllPopups}
            />
            <ImagePopup overlay={overlayClick} isOpen={isImagePopupOpen} onClose={closeAllPopups} card={selectedCard} />
            <Footer />
          </div>
      </CurrentUserContext.Provider>
  );

}

export default App;
